import React, { useState } from "react";
import { Button } from "react-bootstrap";

const Details = ({ data }) => {
  const Link = data[1];
  const tempArea = data[2];
  const tempPerimeter = data[3];
  const [area, setArea] = useState(null)
  const [perimeter,setPerimeter] = useState(null)
  const submitHandler = () => {
     setArea(tempArea[0].toFixed(4));
     setPerimeter(tempPerimeter[0].toFixed(4));
  }
  return (
      <div className="bbox">
        <h4>API</h4>
        <a href={Link}>{Link}</a>
        <Button type="submit" onSubmit={submitHandler}>Details</Button>
        {area && perimeter && (
        <div className="bbox">
            <h4>Area: {area} hectare</h4>
            <h4>Perimeter: {perimeter} Km</h4>
        </div>
        )}
      </div>
  );
};

export default Details;
