import React, { useEffect, useState } from "react";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedState, setSelectedState] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedTaluk, setSelectedTaluk] = useState(null);
  const [selectedHobli, setSelectedHobli] = useState(null);
  const [selectedVillage, setSelectedVillage] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const getOptions = (property, parentValue) => {
    const filteredOptions = parcels
      .filter((parcel) => (parentValue ? parcel[property] === parentValue : true))
      .map((parcel) => ({ value: parcel.Event_id, label: parcel[property] }));

    const uniqueOptions = Array.from(new Map(filteredOptions.map((option) => [option.value, option])).values());
    return uniqueOptions;
  };

  const stateOptions = getOptions("KGISStateName");
  const districtOptions = getOptions("KGISDistrictName", selectedState);
  const talukOptions = getOptions("KGISTalukName", selectedDistrict);
  const hobliOptions = getOptions("KGISHobliName", selectedTaluk);
  const villageOptions = getOptions("KGISVillageName", selectedHobli);

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="stateDropdown">Select State:</label>
        <Select
          className="select"
          id="stateDropdown"
          options={stateOptions}
          value={selectedState}
          onChange={(value) => setSelectedState(value[0])}
          placeholder="Select State"
        />
        <label htmlFor="districtDropdown">Select District:</label>
        <Select
          className="select"
          id="districtDropdown"
          options={districtOptions}
          value={selectedDistrict}
          onChange={(value) => setSelectedDistrict(value[0])}
          placeholder="Select District"
        />
        <label htmlFor="talukDropdown">Select Taluk:</label>
        <Select
          className="select"
          id="talukDropdown"
          options={talukOptions}
          value={selectedTaluk}
          onChange={(value) => setSelectedTaluk(value[0])}
          placeholder="Select Taluk"
        />
        <label htmlFor="hobliDropdown">Select Hobli:</label>
        <Select
          className="select"
          id="hobliDropdown"
          options={hobliOptions}
          value={selectedHobli}
          onChange={(value) => setSelectedHobli(value[0])}
          placeholder="Select Hobli"
        />
        <label htmlFor="villageDropdown">Select Village:</label>
        <Select
          className="select"
          id="villageDropdown"
          options={villageOptions}
          value={selectedVillage}
          onChange={(value) => setSelectedVillage(value[0])}
          placeholder="Select Village"
        />
      </div>
    </div>
  );
};

export default Menu;
