import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./Components/Header";
import { Container, Form } from "react-bootstrap";
import Footer from "./Components/Footer";
import Menu from "./Components/Menu";
import "./assests/App.css";
import Map from "./Components/Map";
import { TileLayer, MapContainer, GeoJSON } from "react-leaflet";
import "leaflet/dist/leaflet.css"; //
import map from "./map.json";

const App = () => {
  const [data, setData] = useState([]);
  const [json, setJson] = useState(null);
  const [link, setLink] = useState(null);
  const [area, setArea] = useState(null)
  const [perimeter, setPerimeter] = useState(null)

  const getData = (data) => {
    console.log(data);
    setData(data);
    setJson(null);
    axios
      .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log(response.data)
        setJson(response.data[0]);
        setLink(response.data[1]);
        setArea(Number(response.data[2].toFixed(4)))
        setPerimeter(Number(response.data[3].toFixed(4)))
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  };
  return (
    <>
      <Header />
      <main className="py-3">
        <Container>
          <Menu onSubmit={getData} />
          {link &&
          <div className="bbox">
              <h4>Api</h4>
              <a href={link} style={{ wordWrap: "break-word" }}>
                {link}<br></br>
              </a>
             <h4> {area} Hectare</h4><br></br>
             <h4> {perimeter} Km</h4>
          </div>}
          {json && <Map data={json} />}
        </Container>
      </main>
      <Footer />
    </>
  );
};

export default App;
