import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [columnOptions, setColumnOptions] = useState({});

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);

        // Initialize columnOptions with options for all columns
        const columns = Object.keys(response.data[0] || {});
        const initialColumnOptions = {};
        columns.forEach((column) => {
          initialColumnOptions[column] = getColumnOptions(
            column,
            response.data
          );
        });
        setColumnOptions(initialColumnOptions);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const handleSelectChange = (value, column) => {
    setSelectedOptions((prevOptions) => ({
      ...prevOptions,
      [column]: value,
    }));

    // Update options for the next column based on the selected value
    const nextColumn = getNextColumn(column);
    if (nextColumn) {
      const updatedOptions = { ...columnOptions };
      updatedOptions[nextColumn] = getColumnOptions(nextColumn, parcels, value);
      setColumnOptions(updatedOptions);
    }
  };

  const getNextColumn = (currentColumn) => {
    const columns = Object.keys(columnOptions);
    const currentIndex = columns.indexOf(currentColumn);
    return currentIndex < columns.length - 1 ? columns[currentIndex + 1] : null;
  };

  const renderDropdowns = () => {
    const columns = Object.keys(parcels[0] || {});

    return columns.map((column) => (
      <div key={column} style={{ marginBottom: "20px" }}>
        <label htmlFor={column}>{column}:</label>
        <Select
          id={column}
          options={columnOptions[column]}
          value={selectedOptions[column]}
          onChange={(value) => handleSelectChange(value, column)}
          isMulti
          placeholder={`Select ${column}`}
        />
      </div>
    ));
  };

  const getColumnOptions = (column, data, filterValue = null) => {
    const uniqueValues = [...new Set(data.map((item) => item[column]))].filter(
      (value) => typeof value === "string" || value instanceof String
    );

    // Apply filtering based on the selected value of the previous column
    if (filterValue) {
      return uniqueValues
        .filter((value) => value === filterValue)
        .map((value) => ({ value, label: value }));
    }

    return uniqueValues.map((value) => ({ value, label: value }));
  };

  return (
    <div>
      {renderDropdowns()}
      <div>
        <h2>Selected Values:</h2>
        {Object.entries(selectedOptions).map(([column, value]) => (
          <p key={column}>
            {column}: {value}
          </p>
        ))}
      </div>
    </div>
  );
};

export default Menu;
