import React, { useState } from "react";
import axios from "axios";
import Header from "./Components/Header";
import { Container } from "react-bootstrap";
import Footer from "./Components/Footer";
import Menu from "./Components/Menu";
import "./assests/App.css";
import Map from "./Components/Map";

const App = () => {
  const [json, setJson] = useState(null);
  const [link, setLink] = useState(null);
  const [tempArea, setTempArea] = useState(null)
  const [tempPerimeter, setTempPerimeter] = useState(null)
  const [area, setArea] = useState(null)
  const [perimeter, setPerimeter] = useState(null)

  const getData = (data) => {
    setJson(null);
    axios
      .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        setJson(response.data[0]);
        setLink(response.data[1]);
        setTempArea(response.data[2])
        setTempPerimeter(response.data[3])
        const tempA = tempArea[0] 
        console.log(tempA)
        setArea(Number(tempA).toFixed(4))
        const tempP = tempPerimeter[0]
        console.log(tempP)
        setPerimeter(Number(tempP).toFixed(4))
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  };
  console.log(area)
  console.log(perimeter)
  return (
    <>
      <Header />
      <main className="py-3">
        <Container>
          <Menu onSubmit={getData} />
          {link && area && perimeter && json && 
          <>
          <Map data={json} />
          <div className="bbox">
              <h4>Api</h4>
              <a href={link} style={{ wordWrap: "break-word" }}>
                {link}
              </a><br></br>
             <h4> {area} Hectare</h4><br></br>
             <h4>{perimeter} Km</h4>
          </div>
          </>
          }
        </Container>
      </main>
      <Footer />
    </>
  );
};

export default App;
