import React, { useState, useEffect } from 'react';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css'; // Make sure to import the Leaflet CSS

function MapContainerComponent({ parcelNo, selectVillage }) {
  const [map, setMap] = useState(null);

  useEffect(() => {
    // Initialize and set up Leaflet map
    const myMap = L.map('mapId').setView([15.505, 76.09], 7);
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(myMap);

    setMap(myMap);

    // Cleanup function
    return () => {
      myMap.remove(); // Remove the map when the component is unmounted
    };
  }, []); // Empty dependency array ensures this effect runs once on mount

  useEffect(() => {
    // Fetch data when selectVillage or parcelNo changes
    axios
      .get(`https://kgis.ksrsac.in:9000/genericwebservices/ws/geomForSurveyNum/${selectVillage[0]?.value}/${parcelNo}/UTM`)
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data

        // Update map or perform other actions with the data here
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, [parcelNo, selectVillage]);

  return <div id="mapId" className="map-container"></div>;
}

export default MapContainerComponent;
