import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);
  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);
  const getUniqueValues = (field) => {
    const uniqueValues = [...new Set(parcels.map((parcel) => parcel[field]))];
    return uniqueValues.map((value) => ({
      value,
      label: `${value}`,
    }));
  };

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="parcelDropdown">Select Parcel:</label>
        <Select
          className="select"
          id="parcelDropdown"
          options={getUniqueValues(KGISStateName)}
          value={selectedParcel}
          placeholder="Select State"
        />
        <Select
          className="select"
          id="parcelDropdown"
          options={distName}
          value={selectedParcel}
          placeholder="Select District"
        />
        <Select
          className="select"
          id="parcelDropdown"
          options={talukName}
          value={selectedParcel}
          placeholder="Select Taluk"
        />
          <Select
          className="select"
          id="parcelDropdown"
          options={hobliName}
          value={selectedParcel}
          placeholder="Select Hobli"
        />
           <Select
          className="select"
          id="parcelDropdown"
          options={villageName}
          value={selectedParcel}
          placeholder="Select Village"
        />
      </div>
    </div>
  );
};

export default Menu;
