import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./Components/Header";
import { Alert, Container } from "react-bootstrap";
import Footer from "./Components/Footer";
import Menu from "./Components/Menu";
import "./assests/App.css";
import Map from "./Components/Map";
import Details from "./Components/Details";

const App = () => {
  const [json, setJson] = useState(null);
  const [data, setData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [showAlert, setShowAlert] = useState(false);

  const getData = (data) => {
    setJson(null);
    if (data !== null) {
      axios
        .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
        .then((response) => {
          if (!response.data) {
            throw new Error(
              "Empty response or response data is not valid JSON"
            );
          }
          setJson(response.data[0]);
          setData(response.data);
        })
        .catch((error) => {
          // Handle CORS error
          if (error.response && error.response.status === 403) {
            setErrorMessage(
              "Access to the resource is forbidden (CORS issue)."
            );
          } else {
            setErrorMessage("Incorrect SurveyNo.");
          }
          console.error("Error fetching data:", error);
        });
    }
  };
  useEffect(() => {
    if (showAlert) {
      const timeout = setTimeout(() => {
        // Hide the alert after 3 seconds
        setShowAlert(false);
        // Refresh the page
        window.location.reload();
      }, 3000);

      // Clear the timeout when the component unmounts or showAlert changes
      return () => clearTimeout(timeout);
    }
  }, [showAlert]);
  const info = data;
  return (
    <>
      <Header />
      <main className="py-3">
        <Container>
          {errorMessage && 
          <Alert variant="danger" show={showAlert}>
            {errorMessage}
            </Alert>}
          <Menu onSubmit={getData} />
          {info && <Details data={info} />}
          {json && <Map data={json} />}
        </Container>
      </main>
      <Footer />
    </>
  );
};

export default App;
