import React, { useEffect, useState } from "react";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedState, setSelectedState] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedTaluk, setSelectedTaluk] = useState(null);
  const [selectedHobli, setSelectedHobli] = useState(null);
  const [selectedVillage, setSelectedVillage] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  // Function to get unique values for a given field
  const getUniqueValues = (field) => {
    const uniqueValues = [...new Set(parcels.map((parcel) => parcel[field]))];
    return uniqueValues.map((value) => ({
      value,
      label: `${value}`,
    }));
  };

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>

        <Select
          className="select"
          id="stateDropdown"
          options={getUniqueValues("KGISStateName")}
          value={selectedState}
          onChange={(values) => {
            setSelectedState(values[0]);
            setSelectedDistrict(null);
            setSelectedTaluk(null);
            setSelectedHobli(null);
            setSelectedVillage(null);
          }}
          placeholder="Select State"
        />

        {selectedState && (
          <Select
            className="select"
            id="districtDropdown"
            options={getUniqueValues("KGISDistrictName")}
            value={selectedDistrict}
            onChange={(values) => {
              setSelectedDistrict(values[0]);
              setSelectedTaluk(null);
              setSelectedHobli(null);
              setSelectedVillage(null);
            }}
            placeholder="Select District"
          />
        )}

        {selectedDistrict && (
          <Select
            className="select"
            id="talukDropdown"
            options={getUniqueValues("KGISTalukName")}
            value={selectedTaluk}
            onChange={(values) => {
              setSelectedTaluk(values[0]);
              setSelectedHobli(null);
              setSelectedVillage(null);
            }}
            placeholder="Select Taluk"
          />
        )}

        {selectedTaluk && (
          <Select
            className="select"
            id="hobliDropdown"
            options={getUniqueValues("KGISHobliName")}
            value={selectedHobli}
            onChange={(values) => {
              setSelectedHobli(values[0]);
              setSelectedVillage(null);
            }}
            placeholder="Select Hobli"
          />
        )}

        {selectedHobli && (
          <Select
            className="select"
            id="villageDropdown"
            options={getUniqueValues("KGISVillageName")}
            value={selectedVillage}
            onChange={(values) => {
              setSelectedVillage(values[0]);
            }}
            placeholder="Select Village"
          />
        )}
      </div>
    </div>
  );
};

export default Menu;
