import React, { useEffect, useState } from "react";
import Select from "react-dropdown-select";
import axios from "axios";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedState, setSelectedState] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedTaluk, setSelectedTaluk] = useState(null);
  const [selectedHobli, setSelectedHobli] = useState(null);
  const [selectedVillage, setSelectedVillage] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const getOptionsByParentId = (parentId) => {
    return parcels.filter((parcel) => parcel.parentId === parentId);
  };

  const stateOptions = parcels
    .filter((parcel) => !parcel.parentId)
    .map((parcel) => ({
      value: parcel.id,
      label: parcel.name,
    }));

  const districtOptions = getOptionsByParentId(selectedState?.value)?.map((parcel) => ({
    value: parcel.id,
    label: parcel.name,
  }));

  const talukOptions = getOptionsByParentId(selectedDistrict?.value)?.map((parcel) => ({
    value: parcel.id,
    label: parcel.name,
  }));

  const hobliOptions = getOptionsByParentId(selectedTaluk?.value)?.map((parcel) => ({
    value: parcel.id,
    label: parcel.name,
  }));

  const villageOptions = getOptionsByParentId(selectedHobli?.value)?.map((parcel) => ({
    value: parcel.id,
    label: parcel.name,
  }));

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="stateDropdown">Select State:</label>
        <Select
          className="select"
          id="stateDropdown"
          options={stateOptions}
          value={selectedState}
          onChange={(selected) => setSelectedState(selected)}
          placeholder="Select State"
        />
        <label htmlFor="districtDropdown">Select District:</label>
        <Select
          className="select"
          id="districtDropdown"
          options={districtOptions}
          value={selectedDistrict}
          onChange={(selected) => setSelectedDistrict(selected)}
          placeholder="Select District"
        />
        <label htmlFor="talukDropdown">Select Taluk:</label>
        <Select
          className="select"
          id="talukDropdown"
          options={talukOptions}
          value={selectedTaluk}
          onChange={(selected) => setSelectedTaluk(selected)}
          placeholder="Select Taluk"
        />
        <label htmlFor="hobliDropdown">Select Hobli:</label>
        <Select
          className="select"
          id="hobliDropdown"
          options={hobliOptions}
          value={selectedHobli}
          onChange={(selected) => setSelectedHobli(selected)}
          placeholder="Select Hobli"
        />
        <label htmlFor="villageDropdown">Select Village:</label>
        <Select
          className="select"
          id="villageDropdown"
          options={villageOptions}
          value={selectedVillage}
          onChange={(selected) => setSelectedVillage(selected)}
          placeholder="Select Village"
        />
      </div>
    </div>
  );
};

export default Menu;
