import React, { useEffect, useState } from "react";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data || !Array.isArray(response.data)) {
          throw new Error("Invalid response data");
        }
        console.log("Fetched data:", response.data);
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const getUniqueOptions = (key) => [
    ...new Map(parcels.map((parcel) => [parcel[key], parcel])).values(),
  ].map((parcel) => ({
    value: parcel.Event_id,
    label: parcel[key],
  }));

  const stateOptions = getUniqueOptions("KGISStateName");
  const distOptions = getUniqueOptions("KGISDistrictName");
  const talukOptions = getUniqueOptions("KGISTalukName");
  const hobliOptions = getUniqueOptions("KGISHobliName");
  const villageOptions = getUniqueOptions("KGISVillageName");

  const handleSelectChange = (selected) => {
    setSelectedParcel(selected[0]?.value || null);
  };

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <Select
          className="select"
          id="stateDropdown"
          options={stateOptions}
          value={selectedParcel}
          onChange={handleSelectChange}
          placeholder="Select State"
        />
        <Select
          className="select"
          id="districtDropdown"
          options={distOptions}
          value={selectedParcel}
          onChange={handleSelectChange}
          placeholder="Select District"
        />
        <Select
          className="select"
          id="talukDropdown"
          options={talukOptions}
          value={selectedParcel}
          onChange={handleSelectChange}
          placeholder="Select Taluk"
        />
        <Select
          className="select"
          id="hobliDropdown"
          options={hobliOptions}
          value={selectedParcel}
          onChange={handleSelectChange}
          placeholder="Select Hobli"
        />
        <Select
          className="select"
          id="villageDropdown"
          options={villageOptions}
          value={selectedParcel}
          onChange={handleSelectChange}
          placeholder="Select Village"
        />
      </div>
    </div>
  );
};

export default Menu;
