import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);
  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const handleSelectChange = (value, column) => {
    setSelectedOptions((prevOptions) => ({
      ...prevOptions,
      [column]: value,
    }));
  };

  const getColumnOptions = (column) => {
    const uniqueValues = [...new Set(parcels.map((parcel) => parcel[column]))];
    return uniqueValues.map((value) => ({ value, label: value }));
  };

  const renderDropdowns = () => {
    const columns = Object.keys(parcels[0] || {});
    return columns.map((column) => (
      <div key={column} style={{ marginBottom: "20px" }}>
        <label htmlFor={column}>{column}:</label>
        <Select
          id={column}
          options={getColumnOptions(column)}
          value={selectedOptions[column]}
          onChange={(value) => handleSelectChange(value, column)}
          isMulti
          placeholder={`Select ${column}`}
        />
      </div>
    ));
  };

  return (
    <div>
      {renderDropdowns()}
      {/* Add other components or display selected values as needed */}
    </div>
  );
};

export default Menu;
