import React, { useState } from "react";
import axios from "axios";
import Header from "./Components/Header";
import { Container } from "react-bootstrap";
import Footer from "./Components/Footer";
import Menu from "./Components/Menu";
import "./assests/App.css";
import Map from "./Components/Map";
import Details from "./Components/Details";

const App = () => {
  const [json, setJson] = useState(null);
  const [data, setData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const getData = (data) => {
    setJson(null);
    if (data !== null) {
      axios
        .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
        .then((response) => {
          if (!response.data) {
            throw new Error(
              "Empty response or response data is not valid JSON"
            );
          }
          setJson(response.data[0]);
          setData(response.data);
        })
        .catch((error) => {
          // Handle CORS error
          if (error.response && error.response.status === 403) {
            setErrorMessage(
              "Access to the resource is forbidden (CORS issue)."
            );
          } else {
            setErrorMessage("An error occurred while fetching data.");
          }
          console.error("Error fetching data:", error);
        });
    }
  };
  const info = data;
  return (
    <>
      <Header />
      <main className="py-3">
        <Container>
          {errorMessage && (
            <div style={{ color: "red", marginTop: "10px" }}>
              {errorMessage}
            </div>
          )}
          <Menu onSubmit={getData} />
          {info && <Details data={info} />}
          {json && <Map data={json} />}
        </Container>
      </main>
      <Footer />
    </>
  );
};

export default App;
