import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";
const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);

  useEffect(() => {
    // Fetch all parcels from the Flask API
    fetch('/parcels')
      .then(response => response.json())
      .then(data => setParcels(data))
      .catch(error => console.error('Error fetching parcels:', error));
  }, []);
  return (  
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <Form>
          <Select className="select" placeholder="State">
            {parcels.map(parcel =>
            <option key = {parcel.KGISStateID} value = {parcel.KGISStateID}>
              {parcel.KGISVillageName}
            </option>
            )}
          </Select>
          {/*
          <Select className="select" options={options} placeholder="District" />

          <Select className="select" options={options} placeholder="Hobli" />

          <Select className="select" options={options} placeholder="Village" />
            */}
          <FormControl
            className="surveyno"
            type="number"
            placeholder="Survey no"
          />

          <Button type="Submit">Submit</Button>
        </Form>
        <div className="bbox">
          <h4>API:</h4>
        </div>
      </div>
    </div>
  );
};

export default Menu;
