import React, { useState, useEffect } from "react";
import axios from "axios";
import { Alert, Button } from "react-bootstrap";
import Menu from "./Menu";
import Map from "./Map";
import Details from "./Details";

const HomeScreen = () => {
  const [json, setJson] = useState(null);
  const [data, setData] = useState(null);
  const [villageInfo, setVillageInfo] = useState([])
  const [errorMessage, setErrorMessage] = useState("");
  const [showAlert, setShowAlert] = useState(false);

  const getData = (data) => {
    setJson(null);
    setVillageInfo(data)
    if (data !== null) {
      axios
        .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
        .then((response) => {
          if (!response.data) {
            throw new Error(
              "Empty response or response data is not valid JSON"
            );
          }
          setJson(response.data[0]);
          setData(response.data);
        })
        .catch((error) => {
          // Handle CORS error
          if (error.response && error.response.status === 403) {
            setErrorMessage(
              "Access to the resource is forbidden (CORS issue)."
            );
          } else {
            setErrorMessage("Incorrect SurveyNo.");
          }
          setShowAlert(true);
          console.error("Error fetching data:", error);
        });
    }
  };
  useEffect(() => {
    if (showAlert) {
      const timeout = setTimeout(() => {
        setShowAlert(false);
        window.location.reload();
      }, 3000);
      return () => clearTimeout(timeout);
    }
  }, [showAlert]);

  const info = data;
  const clickHandler = async () => {
    try {
      const apiResponse = await axios.get(
        `http://localhost:5000/downloads/${villageInfo[1]}/${villageInfo[0]}`
      );

      const blob = new Blob([apiResponse.data], { type: "image/png" });
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = url;
      link.download = "plot.png";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error("Error:", error.message);
    }
  };
  return (
    <>
      {errorMessage && (
        <Alert className="alert" variant="danger">
          <div className="alert-content">{errorMessage}</div>
        </Alert>
      )}
      <Menu onSubmit={getData} />
      {info && <Details data={info} />}
      {json && (
      <>
      <Map data={json} />
      <div className="bbox">
        <Button onClick={clickHandler}>
        Download Image
      </Button>
      </div>
      </>)}
    </>
  );
};

export default HomeScreen;
