import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedState, setSelectedState] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedTaluk, setSelectedTaluk] = useState(null);
  const [selectedHobli, setSelectedHobli] = useState(null);
  const [selectedVillage, setSelectedVillage] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const filterOptions = (options, selectedValue) => {
    return options.filter((option) => option.parentId === selectedValue);
  };

  const stateOptions = parcels
    .filter((parcel) => !parcel.parentId)
    .map((parcel) => ({
      value: parcel.KGISStateID,
      label: `${parcel.KGISStateName}`,
    }));

  const districtOptions = filterOptions(
    parcels.filter((parcel) => parcel.KGISStateID === selectedState),
    selectedState
  ).map((parcel) => ({
    value: parcel.KGISDistrictID,
    label: `${parcel.KGISDistrictName}`,
  }));

  const talukOptions = filterOptions(
    parcels.filter((parcel) => parcel.KGISDistrictID === selectedDistrict),
    selectedDistrict
  ).map((parcel) => ({
    value: parcel.KGISTalukID,
    label: `${parcel.KGISTalukName}`,
  }));

  const hobliOptions = filterOptions(
    parcels.filter((parcel) => parcel.KGISTalukID === selectedTaluk),
    selectedTaluk
  ).map((parcel) => ({
    value: parcel.KGISHobliID,
    label: parcel.KGISHobliName,
  }));

  const villageOptions = filterOptions(
    parcels.filter((parcel) => parcel.KGISHobliID === selectedHobli),
    selectedHobli
  ).map((parcel) => ({
    value: parcel.KGISVillageID,
    label: parcel.KGISVillageName,
  }));

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="stateDropdown">Select State:</label>
        <Select
          className="select"
          id="stateDropdown"
          options={stateOptions}
          value={selectedState}
          onChange={(selected) => {
            setSelectedState(selected[0]?.value || null);
            setSelectedDistrict(null);
            setSelectedTaluk(null);
            setSelectedHobli(null);
            setSelectedVillage(null);
          }}
          placeholder="Select State"
        />

        {selectedState && (
          <>
            <label htmlFor="districtDropdown">Select District:</label>
            <Select
              className="select"
              id="districtDropdown"
              options={districtOptions}
              value={selectedDistrict}
              onChange={(selected) => {
                setSelectedDistrict(selected[0]?.value || null);
                setSelectedTaluk(null);
                setSelectedHobli(null);
                setSelectedVillage(null);
              }}
              placeholder="Select District"
            />
          </>
        )}

        {selectedDistrict && (
          <>
            <label htmlFor="talukDropdown">Select Taluk:</label>
            <Select
              className="select"
              id="talukDropdown"
              options={talukOptions}
              value={selectedTaluk}
              onChange={(selected) => {
                setSelectedTaluk(selected[0]?.value || null);
                setSelectedHobli(null);
                setSelectedVillage(null);
              }}
              placeholder="Select Taluk"
            />
          </>
        )}

        {selectedTaluk && (
          <>
            <label htmlFor="hobliDropdown">Select Hobli:</label>
            <Select
              className="select"
              id="hobliDropdown"
              options={hobliOptions}
              value={selectedHobli}
              onChange={(selected) => {
                setSelectedHobli(selected[0]?.value || null);
                setSelectedVillage(null);
              }}
              placeholder="Select Hobli"
            />
          </>
        )}

        {selectedHobli && (
          <>
            <label htmlFor="villageDropdown">Select Village:</label>
            <Select
              className="select"
              id="villageDropdown"
              options={villageOptions}
              value={selectedVillage}
              onChange={(selected) =>
                setSelectedVillage(selected[0]?.value || null)
              }
              placeholder="Select Village"
            />
          </>
        )}
      </div>
    </div>
  );
};

export default Menu;
