import React, { useState, useEffect } from "react";
import axios from "axios";
import Map from "./Map";
import Details from "./Details";
import Download from "./Download";
import { Alert } from "react-bootstrap";
import Menu from "./Menu";

const HomeScreen = (props) => {
  const [json, setJson] = useState(null);
  const [data, setData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [showAlert, setShowAlert] = useState(false);
  const [villageData, setVillageData] = useState([])
  const getData = (data) => {
    setJson(null);
    setVillageData(data)
    if (data !== null) {
      axios
        .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
        .then((response) => {
          if (!response.data) {
            throw new Error(
              "Empty response or response data is not valid JSON"
            );
          }
          setJson(response.data[0]);
          setData(response.data);
        })
        .catch((error) => {
          // Handle CORS error
          if (error.response && error.response.status === 403) {
            setErrorMessage(
              "Access to the resource is forbidden (CORS issue)."
            );
          } else {
            setErrorMessage("Incorrect SurveyNo.");
          }
          setShowAlert(true);
          console.error("Error fetching data:", error);
        });
    }
  };
  useEffect(() => {
    if (showAlert) {
      const timeout = setTimeout(() => {
        setShowAlert(false);
        window.location.reload();
      }, 3000);
      return () => clearTimeout(timeout);
    }
  }, [showAlert]);
  const info = data;
  const pdfData = [data, villageData]
  props.onClick(pdfData)
  return (
    <>
      {errorMessage && (
        <Alert className="alert" variant="danger">
          <div className="alert-content">{errorMessage}</div>
        </Alert>
      )}
      <Menu onSubmit={getData} />
      {info && <Details data={info} />}
      {json && <Map data={json} />}
      {data && <Download data={pdfData} onClick={getData}/>}
    </>
  );
};

export default HomeScreen;
