import React, { useState, useEffect } from 'react';
import Select from 'react-select';

const Test = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);

  useEffect(() => {
    // Fetch all parcels from the Flask API
    fetch('/parcel')
      .then(response => response.json())
      .then(data => setParcels(data))
      .catch(error => console.error('Error fetching parcels:', error));
  }, []);

  const options = parcels.map(parcel => ({
    value: parcel.Event_id,
    label: parcel.KGISVillageName
  }));

  const handleSelectChange = selectedOption => {
    setSelectedParcel(selectedOption);
  };

  return (
    <div>
      <label htmlFor="parcelDropdown">Select Parcel:</label>
      <Select
        id="parcelDropdown"
        options={options}
        value={selectedParcel}
        onChange={handleSelectChange}
        placeholder="Select a parcel"
      />

      {selectedParcel && (
        <div>
          <h2>Selected Parcel Details:</h2>
          <p>Event ID: {selectedParcel.value}</p>
          <p>KGISVillageName: {selectedParcel.label}</p>
          {/* Add other details as needed */}
        </div>
      )}
    </div>
  );
};

export default Test;
