import React, { useRef } from "react";
import { TileLayer, MapContainer, GeoJSON } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Make sure to import the Leaflet CSS
import data from '../map.json'

const Map = ({data})=> {
  const mapRef = useRef(null);

  if (mapRef.current && data !== null ) {
    // Create a GeoJSON layer with the fetched data
    const geoJsonLayer = new L.GeoJSON(data);

    // Get the bounds of the GeoJSON layer
    const bounds = geoJsonLayer.getBounds();

    // Access the Leaflet map instance using the ref and fit the map to the bounds
    mapRef.current.fitBounds(bounds);
  }
  const setColor = ({ properties }) => {
    return { weight: 1 , color: 'red'};
  };

  return (
      <MapContainer
        center={[ 13.21,76.15]}
        zoom={7}
        className="map-container"
        whenCreated={(map) => (mapRef.current = map)}
        
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        <GeoJSON data={data} style={setColor}/>
      </MapContainer>
  );
}

export default Map;
