import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);
  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);
  const State = [
    ...new Map(
      parcels.map((parcel) => [
        parcel.KGISStateName,
        parcel,
      ])
    ).values(),
  ];
  const District = [
    ...new Map(
      parcels.map((parcel) => [
        parcel.KGISDistrictName,
        parcel,
      ])
    ).values(),
  ];

  const stateName = State.map((parcel) => ({
    value: parcel.Event_id,
    label: `${parcel.KGISStateName}`,
  }));
  const distName = District.map((parcel) => ({
    value: parcel.Event_id,
    label: `${parcel.KGISDistrictName}`,
  }));

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="parcelDropdown">Select Parcel:</label>
        <Select
          className="select"
          id="parcelDropdown"
          options={stateName}
          value={selectedParcel}
          placeholder="Select a parcel"
        />
        <Select
          className="select"
          id="parcelDropdown"
          options={distName}
          value={selectedParcel}
          placeholder="Select a parcel"
        />
      </div>
    </div>
  );
};

export default Menu;
