import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);
  const [parcelNo, setParcelNo] = useState([])
  const [selectState, setSelectState] = useState(null)
  const [selectDistrict, setSelectDistrict] = useState([])
  const [selectTaluk, setSelectTaluk] = useState([])
  const [selectHobli, setSelectHobli] = useState([])
  const [selctVillage, setSelctVillage] = useState(null)
  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);
  const State = [
    ...new Map(
      parcels.map((parcel) => [parcel.KGISStateName, parcel])
    ).values(),
  ];
  const District = [
    ...new Map(
      parcels.map((parcel) => [parcel.KGISDistrictName, parcel])
    ).values(),
  ];
  const Taluk = [
    ...new Map(
      parcels.map((parcel) => [parcel.KGISTalukID, parcel])
    ).values(),
  ];
  const Hobli = [
    ...new Map(
      parcels.map((parcel) => [parcel.KGISHobliID, parcel])
    ).values(),
  ];
  const Village = [
    ...new Map(
      parcels.map((parcel) => [parcel.KGISVillageID, parcel])
    ).values(),
  ];

  const stateName = State.map((parcel) => ({
    value: parcel.KGISStateID,
    label: `${parcel.KGISStateName}`,
  }));
  const distName = District.map((parcel) => ({
    value: parcel.KGISDistrictID,
    label: `${parcel.KGISDistrictName}`,
  }));
  const talukName = Taluk.map((parcel) => ({
    value: parcel.KGISTalukID,
    label: `${parcel.KGISTalukName}`,
  }));
  const hobliName = Hobli.map(parcel => ({
    value: parcel.KGISHobliID,
    label: parcel.KGISHobliName
  }));
  const villageName = Village.map(parcel => ({
    value: parcel.KGISVillageID,
    label: parcel.KGISVillageName
  }));
  const changeHandler = (e) => {
    setSelctVillage(e.target.value)
    console.log(e.target.value)
  }


  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="parcelDropdown">Select Parcel:</label>
        <Select
          className="select"
          id="parcelDropdown"
          options={stateName}
          value={selectState}
          placeholder="Select State"
        />
        <Select
          className="select"
          id="parcelDropdown"
          options={distName}
          value={selectDistrict}
          placeholder="Select District"
        />
        <Select
          className="select"
          id="parcelDropdown"
          options={talukName}
          value={selectedParcel}
          placeholder="Select Taluk"
        />
          <Select
          className="select"
          id="parcelDropdown"
          options={hobliName}
          value={selectedParcel}
          placeholder="Select Hobli"
        />
           <Select
          className="select"
          id="parcelDropdown"
          options={villageName}
          value={selctVillage}
          onChange={changeHandler}
          placeholder="Select Village"
        />
        <Form>
         <FormControl
            className="surveyno"
            type="number"
            value={parcelNo}
            placeholder="Survey no"
          />

          <Button type="Submit">Submit</Button>
        </Form>
      </div>
    </div>
  );
};

export default Menu;
