import React, { useEffect, useState } from "react";
import axios from "axios";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedState, setSelectedState] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedTaluk, setSelectedTaluk] = useState(null);
  const [selectedHobli, setSelectedHobli] = useState(null);
  const [selectedVillage, setSelectedVillage] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  const getOptionsByParentId = (parentId) => {
    return parentId
      ? parcels.filter((parcel) => parcel.parentId === parentId)
      : [];
  };

  const stateOptions = parcels
    .filter((parcel) => !parcel.parentId)
    .map((parcel) => (
      <option key={parcel.id} value={parcel.id}>
        {parcel.name}
      </option>
    ));

  const districtOptions = getOptionsByParentId(selectedState?.value).map(
    (parcel) => (
      <option key={parcel.id} value={parcel.id}>
        {parcel.name}
      </option>
    )
  );

  const talukOptions = getOptionsByParentId(selectedDistrict?.value).map(
    (parcel) => (
      <option key={parcel.id} value={parcel.id}>
        {parcel.name}
      </option>
    )
  );

  const hobliOptions = getOptionsByParentId(selectedTaluk?.value).map(
    (parcel) => (
      <option key={parcel.id} value={parcel.id}>
        {parcel.name}
      </option>
    )
  );

  const villageOptions = getOptionsByParentId(selectedHobli?.value).map(
    (parcel) => (
      <option key={parcel.id} value={parcel.id}>
        {parcel.name}
      </option>
    )
  );

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="stateDropdown">Select State:</label>
        <select
          className="select"
          id="stateDropdown"
          value={selectedState?.value}
          onChange={(e) => setSelectedState({ value: e.target.value })}
        >
          <option value="">Select State</option>
          {stateOptions}
        </select>

        <label htmlFor="districtDropdown">Select District:</label>
        <select
          className="select"
          id="districtDropdown"
          value={selectedDistrict?.value}
          onChange={(e) => setSelectedDistrict({ value: e.target.value })}
        >
          <option value="">Select District</option>
          {districtOptions}
        </select>

        <label htmlFor="talukDropdown">Select Taluk:</label>
        <select
          className="select"
          id="talukDropdown"
          value={selectedTaluk?.value}
          onChange={(e) => setSelectedTaluk({ value: e.target.value })}
        >
          <option value="">Select Taluk</option>
          {talukOptions}
        </select>

        <label htmlFor="hobliDropdown">Select Hobli:</label>
        <select
          className="select"
          id="hobliDropdown"
          value={selectedHobli?.value}
          onChange={(e) => setSelectedHobli({ value: e.target.value })}
        >
          <option value="">Select Hobli</option>
          {hobliOptions}
        </select>

        <label htmlFor="villageDropdown">Select Village:</label>
        <select
          className="select"
          id="villageDropdown"
          value={selectedVillage?.value}
          onChange={(e) => setSelectedVillage({ value: e.target.value })}
        >
          <option value="">Select Village</option>
          {villageOptions}
        </select>
      </div>
    </div>
  );
};

export default Menu;
