import React, { useRef, useState } from "react";
import { TileLayer, MapContainer, GeoJSON } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Make sure to import the Leaflet CSS
import data from '../map.json'

const Map = ({data})=> {

  const [lat, setLat] = useState(null)
  const [lon, setLon] = useState(null)
  const [center, setCenter] = useState([])
  if (data !== null) {
    const longitude = data.bbox[0]
    setLon(longitude.toFixed(4))
    const latitude = data.bbox[1]
    setLat(latitude.toFixed(4))
  } else {
    setCenter([13.21,76.15])
  }

  const setColor = ({ properties }) => {
    return { weight: 1 , color: 'red'};
  };
  console.log(lon)
  console.log(data.bbox[1])

  return (
      <MapContainer
        center={[15.332263047614394, 76.23873029090704]}
        zoom={7}
        className="map-container"
        
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        <GeoJSON data={data} style={setColor}/>
      </MapContainer>
  );
}

export default Map;
