import React, {useEffect} from "react";
import axios from "axios";
import Header from "./Components/Header";
import { Container, Form } from "react-bootstrap";
import Footer from "./Components/Footer";
import Menu from "./Components/Menu";
import "./assests/App.css";
import Map from './Components/Map'
import { TileLayer, MapContainer, GeoJSON } from "react-leaflet";
import "leaflet/dist/leaflet.css"; // 

const App = () => {
  const getData = (data) => {
    console.log(data)
     globalparcel = Number(data[0])
    globalvillageId = Number(data[1])
  }
  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get(`http://127.0.0.1:5000//parcelapi/${globalparcel}/${globalvillageId}/UTM`)
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);

  return (
    <>
      <Header />
      <main className="py-3">
        <Container>
          <Menu onSubmit = {getData}/>
          <MapContainer
        center={[14.00, 77.22]}
        zoom={7}
        className="map-container"
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
      </MapContainer>
        </Container>
      </main>
      <Footer />
    </>
  );
};

export default App;
