import React, { useState } from "react";
import { Button } from "react-bootstrap"; 
import Download from "./Download";

const Details = ({ data }) => {
  const [link, setLink] = useState(null)
  const [tempArea, setTempArea]= useState([])
  const [tempPerimeter, setTempPerimeter] = useState([])
  const [idata, setIdata] = useState([])
  useEffect(() => {
    // Update state only if it's not already set
    if (!idata.length) {
      setIdata(data1);
    }
  }, [data1]);
  const area = tempArea[0].toFixed(4);
  const perimeter = tempPerimeter[0].toFixed(4);
  const [downloadData, setDownloadData] = useState([])
  const submitHandler = () => {
    const sendData = idata 
    setDownloadData(sendData)
  }
  return (
    <div className="container-menu">
    <div className="bbox">
      <h4>API</h4>
      <a href={link}>{link}</a>
      <br/><br/>
      <h3>Details</h3>
      <br/>
      <h4>Area: {area} hectare</h4>
      <br/>
      <h4>Perimeter: {perimeter} Km</h4>
      <Button onClick={submitHandler} >
          Download
          </Button>
          {downloadData && <Download data={downloadData} />}
      </div>
    </div>
  );
};

export default Details;
