import React, { useState, useEffect } from 'react';
import axios from 'axios';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapContainerComponent = ({ parcelNo, selectVillage }) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    const myMap = L.map('mapId').setView([15.505, 76.09], 7);
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(myMap);
    setMap(myMap);

    return () => {
      myMap.remove();
    };
  }, []);

  useEffect(() => {
    axios
      .get(`https://kgis.ksrsac.in:9000/genericwebservices/ws/geomForSurveyNum/${selectVillage[0].value}/${parcelNo}/UTM`)
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, [parcelNo, selectVillage]);

  return <div id="mapId" className="map-container"></div>;
};

export default MapContainerComponent;
