import React, {useState,useEffect } from "react";
import { TileLayer, MapContainer, GeoJSON } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Make sure to import the Leaflet CSS
import data from "../map.json";

const Map = ({ data }) => {
  const [map, setMap] = useState(null);
  useEffect(() => {
    console.log("Effect triggered");
    if (map && data && data.features.length > 0) {
      const bounds = L.geoJSON(data).getBounds();
      map.fitBounds(bounds);
      console.log(map)
    }
  }, [map, data]);
  
  
  const setColor = ({ properties }) => {
    return { weight: 2, color: "red" };
  };

  return (
    <MapContainer
      center={[15.3323, 76.2387]}
      zoom={12}
      className="map-container"
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      />
      <GeoJSON data={data} style={setColor} />
    </MapContainer>
  );
};

export default Map;
