import React, { useEffect, useState } from "react";
import {
  Form,
  FormGroup,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import axios from "axios";
import Select from "react-dropdown-select";
import "../assests/App.css";

const Menu = () => {
  const [parcels, setParcels] = useState([]);
  const [selectedParcel, setSelectedParcel] = useState(null);
  const [selectedState, setSelectedState] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState(null);
  const [selectedTaluk, setSelectedTaluk] = useState(null);
  const [selectedHobli, setSelectedHobli] = useState(null);
  const [selectedVillage, setSelectedVillage] = useState(null);

  useEffect(() => {
    // Fetch all parcels using Axios
    axios
      .get("http://127.0.0.1:5000/parcel")
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log("Fetched data:", response.data); // Log the fetched data
        setParcels(response.data);
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }, []);
  
  const State = [
    ...new Map(
      parcels.map((parcel) => [parcel.KGISStateName, parcel])
    ).values(),
  ];
  const getFilteredOptions = (options, selectedValue) => {
    return options.filter((option) => option.parentId === selectedValue);
  };

  const stateName = State.map((parcel) => ({
    value: parcel.Event_id,
    label: `${parcel.KGISStateName}`,
  }));
  const distName = getFilteredOptions(
    parcels.map((parcel) => ({
      value: parcel.KGISDistrictID,
      label: parcel.KGISDistrictName,
      parentId: parcel.KGISStateID,
    })),
    selectedState
  );
  const talukName = getFilteredOptions(
    parcels.map((parcel) => ({
      value: parcel.KGISTalukID,
      label: parcel.KGISTalukName,
      parentId: parcel.KGISDistrictID,
    })),
    selectedDistrict
  );
  const hobliName = getFilteredOptions(
    parcels.map((parcel) => ({
      value: parcel.KGISHobliID,
      label: parcel.KGISHobliName,
      parentId: parcel.KGISTalukID,
    })),
    selectedTaluk
  );
  const villageName =getFilteredOptions(
    parcels.map((parcel) => ({
      value: parcel.KGISVillageID,
      label: parcel.KGISVillageName,
      parentId: parcel.KGISHobliID,
    })),
    selectedHobli
  );

  return (
    <div className="menu">
      <div className="container">
        <h1>Select the Farm</h1>
        <label htmlFor="parcelDropdown">Select Parcel:</label>
        <Select
          className="select"
          id="stateDropdown"
          options={stateName}
          value={selectedState}
          onChange={(selected) => setSelectedState(selected)}
          placeholder="Select State"
        />
        
        <Select
          className="select"
          id="districtDropdown"
          options={distName}
          value={selectedDistrict}
          onChange={(selected) => setSelectedDistrict(selected)}
          placeholder="Select District"
        />
        
        <Select
          className="select"
          id="talukDropdown"
          options={talukName}
          value={selectedTaluk}
          onChange={(selected) => setSelectedTaluk(selected)}
          placeholder="Select Taluk"
        />
       
        <Select
          className="select"
          id="hobliDropdown"
          options={hobliName}
          value={selectedHobli}
          onChange={(selected) => setSelectedHobli(selected)}
          placeholder="Select Hobli"
        />
       
        <Select
          className="select"
          id="villageDropdown"
          options={villageName}
          value={selectedVillage}
          onChange={(selected) => setSelectedVillage(selected)}
          placeholder="Select Village"
        />
      </div>
    </div>
  );
};

export default Menu;
