import React, { useState, useEffect } from "react";
import { TileLayer, MapContainer, GeoJSON } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Make sure to import the Leaflet CSS
import data from "../map.json";

const Map = ({ data }) => {
  const [bounds, setBounds] = useState([
    [0, 0],
    [0, 0],
  ]);

  useEffect(() => {
    if (data && data.features.length > 0) {
      const coordinates = data.features.reduce(
        (acc, feature) => acc.concat(feature.geometry.coordinates),
        []
      );

      const newBounds = coordinates.reduce(
        (acc, coord) => [
          [Math.min(acc[0][0], coord[1]), Math.min(acc[0][1], coord[0])],
          [Math.max(acc[1][0], coord[1]), Math.max(acc[1][1], coord[0])],
        ],
        [
          [Infinity, Infinity],
          [-Infinity, -Infinity],
        ]
      );

      setBounds(newBounds);
    }
  }, [data]);

  // Calculate the center and zoom based on the bounds
  const center = [
    (bounds[0][0] + bounds[1][0]) / 2,
    (bounds[0][1] + bounds[1][1]) / 2,
  ];
  const zoom = Math.min(
    Math.abs(bounds[1][0] - bounds[0][0]),
    Math.abs(bounds[1][1] - bounds[0][1])
  );

  const setColor = ({ properties }) => {
    return { weight: 1 };
  };

  return (
    <MapContainer center={zoom} zoom={7} className="map-container">
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      />
      <GeoJSON data={data} style={setColor} />
    </MapContainer>
  );
};

export default Map;
