import React, { useState } from "react";
import axios from "axios";
import Header from "./Components/Header";
import { Container } from "react-bootstrap";
import Footer from "./Components/Footer";
import Menu from "./Components/Menu";
import "./assests/App.css";
import Map from "./Components/Map";

const App = () => {
  const [json, setJson] = useState(null);
  const [data,setData] = useState([])
  const [link, setLink] = useState(null);
  const [tempArea, setTempArea] = useState([])
  const [tempPerimeter, setTempPerimeter] = useState([])
  const [area, setArea] = useState(null)
  const [perimeter, setPerimeter] = useState(null)

  const getData = (data) => {
    setJson(null);
    setData(data)
    if (data !== null){
    axios
      .get(`http://localhost:5000/parcelapi/${data[1]}/${data[0]}`)
      .then((response) => {
        if (!response.data) {
          throw new Error("Empty response or response data is not valid JSON");
        }
        console.log(response.data)
        setJson(response.data[0]);
        setLink(response.data[1]);
        setTempArea(response.data[2])
        setTempPerimeter(response.data[3])
        console.log(response.data)
        const tempA = tempArea 
        console.log(tempA)
        setArea(Number(tempA).toFixed(4))
        const tempP = tempPerimeter
        console.log(tempP)
        setPerimeter(Number(tempP).toFixed(4))
      })
      .catch((error) => console.error("Error fetching parcels:", error));
  }};
  console.log(area)
  console.log(perimeter)
  return (
    <>
      <Header />
      <main className="py-3">
        <Container>
          <Menu onSubmit={getData} />
          {json && <Map data={json} />}
        </Container>
      </main>
      <Footer />
    </>
  );
};

export default App;
